mapper.update:

update market set
	title = #{title }
	, mobile = #{mobile }
	, head_code = #{head_code }
	, item_code = #{item_code }
	, content = #{content }
	, price = #{price }
	, thumbnail = #{thumbnail }
where market_no = #{market_no }
and writer = #{writer }

update market set use_yn = 'N'
where writer = #{writer }
and market_no = #{market_no }

mapper.insert:
insert into marketreply values(
	seq_marketreply.nextval
	, #{market_no }
	, #{writer }
	, #{content }
	, sysdate
	, 'Y' )

mapper.update:
update marketreply set content = #{content }
where writer = #{writer }
and market_no = #{market_no }
and reply_code = #{reply_code }

update marketreply set use_yn = 'N'
where writer = #{writer }
and reply_code = #{reply_code }
